// Format analysis to HTML with color coding
function formatAnalysisToHtml(analysis, aiName, colorClass) {
  let htmlContent = analysis
    // Headers
    .replace(/^# (.+)$/gm, `<h2 class="${colorClass}-header-main">$&</h2>`)
    .replace(/^## (.+)$/gm, `<h2 class="${colorClass}-header">$&</h2>`)
    .replace(/^### (.+)$/gm, `<h3 class="${colorClass}-subheader">$&</h3>`)
    .replace(/^#### (.+)$/gm, `<h4 class="${colorClass}-subheader-small">$&</h4>`)

    // Ticker symbols
    .replace(/\*\*([A-Z]{2,5})\*\*/g, `<span class="ticker ${colorClass}">$&</span>`)

    // Action indicators
    .replace(/‚úÖ/g, '<span class="emoji-green">‚úÖ</span>')
    .replace(/‚ö†Ô∏è/g, '<span class="emoji-yellow">‚ö†Ô∏è</span>')
    .replace(/‚ùå|üõë/g, '<span class="emoji-red">‚ùå</span>')

    // Bold text
    .replace(/\*\*(.+?)\*\*/g, '<strong>$&</strong>')

    // Code blocks
    .replace(/```([\s\S]*?)```/g, '<pre class="code-block">$&</pre>')

    // Lists
    .replace(/^- (.+)$/gm, '<li>$&</li>')
    .replace(/^[0-9]+[.] (.+)$/gm, '<li class="numbered">$1</li>')

    // Percentages with color coding
    .replace(/\+[0-9.]+%/g, '<span class="positive">$&</span>')
    .replace(/-[0-9.]+%/g, '<span class="negative">$&</span>')

    // Dollar amounts
    .replace(/\$[0-9,]+[.]?[0-9]*/g, '<span class="money">$$$&</span>')

    // Paragraphs
    .replace(/\n\n+/g, '</p><p>')
    .replace(/\n/g, '<br>');

  // Wrap list items
  htmlContent = htmlContent.replace(/(<li>.*?<\/li>)+/g, '<ul>$&</ul>');
  htmlContent = htmlContent.replace(/(<li class="numbered">.*?<\/li>)+/g, '<ol>$&</ol>');

  return htmlContent;
}

