const myGuid =  $input.first().json.guid;

// Store matched posts
let matchedPosts = [];

// Recursive function to process paginated data
function checkPosts(posts) {
  for (const post of posts) {
    if (post.meta && post.meta.n8n_guid === myGuid) {
      matchedPosts.push(post);
    }
  }
}

// Get input data (assumes response JSON is already in $json)
checkPosts($json.body);

// Return matched posts
return {
  posts: matchedPosts,
  next_page: parseInt($input.first().json.current_page) + 1,
  total_pages: parseInt($input.first().json.headers["x-wp-totalpages"]),
  guid: $input.first().json.guid
};

